import React, { useState, useEffect, useRef } from 'react';
import { 
  Shield, 
  AlertTriangle, 
  Activity, 
  MessageSquare, 
  Send,
  RefreshCw,
  Eye,
  Ban,
  Zap,
  TrendingUp,
  Server,
  Users,
  Globe,
  Clock,
  CheckCircle,
  XCircle,
  AlertCircle,
  Info,
  Search,
  Filter,
  Download,
  Settings
} from 'lucide-react';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell, BarChart, Bar } from 'recharts';
import api from './api';

function App() {
  const [logs, setLogs] = useState([]);
  const [analysis, setAnalysis] = useState(null);
  const [chatHistory, setChatHistory] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(false);
  const [autoRefresh, setAutoRefresh] = useState(true);
  const [selectedTab, setSelectedTab] = useState('dashboard');
  const chatContainerRef = useRef(null);

  // Fetch data functions
  const fetchLogs = async () => {
    try {
      console.log('Fetching logs...');
      const data = await api.getLogs({ limit: 50 });
      console.log('Logs received:', data?.length);
      setLogs(data);
    } catch (error) {
      console.error('Failed to fetch logs:', error);
    }
  };

  const fetchAnalysis = async () => {
    try {
      console.log('Fetching analysis...');
      const data = await api.getAnalysis();
      console.log('Analysis received:', data);
      setAnalysis(data);
    } catch (error) {
      console.error('Failed to fetch analysis:', error);
    }
  };

  const fetchStats = async () => {
    try {
      console.log('Fetching stats...');
      const data = await api.getStats();
      console.log('Stats received:', data);
      setStats(data);
    } catch (error) {
      console.error('Failed to fetch stats:', error);
    }
  };

  const handleSendMessage = async () => {
    if (!currentMessage.trim()) return;

    const userMessage = { type: 'user', content: currentMessage };
    setChatHistory(prev => [...prev, userMessage]);
    setCurrentMessage('');
    setLoading(true);

    try {
      const response = await api.chat(currentMessage);
      const aiMessage = { type: 'ai', content: response.answer };
      setChatHistory(prev => [...prev, aiMessage]);
    } catch (error) {
      const errorMessage = { type: 'ai', content: 'Sorry, I encountered an error. Please try again.' };
      setChatHistory(prev => [...prev, errorMessage]);
    }
    setLoading(false);
  };

  // Auto-refresh effect
  useEffect(() => {
    fetchLogs();
    fetchAnalysis();
    fetchStats();

    if (autoRefresh) {
      const interval = setInterval(() => {
        fetchLogs();
        fetchAnalysis();
        fetchStats();
      }, 10000); // Refresh every 10 seconds
      return () => clearInterval(interval);
    }
  }, [autoRefresh]);

  // Auto-scroll chat
  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [chatHistory]);

  const getSeverityColor = (severity) => {
    switch (severity?.toLowerCase()) {
      case 'critical': return 'severity-critical';
      case 'high': return 'severity-high';
      case 'medium': return 'severity-medium';
      case 'low': return 'severity-low';
      default: return 'severity-low';
    }
  };

  const formatTimestamp = (timestamp) => {
    return new Date(timestamp).toLocaleString();
  };

  // Generate chart data from logs
  const generateChartData = () => {
    if (!logs.length) return [];

    const hourlyData = {};
    logs.forEach(log => {
      const hour = new Date(log.timestamp).toISOString().slice(0, 13) + ':00:00Z';
      if (!hourlyData[hour]) {
        hourlyData[hour] = { time: hour, count: 0 };
      }
      hourlyData[hour].count++;
    });

    return Object.values(hourlyData).sort((a, b) => new Date(a.time) - new Date(b.time));
  };

  return (
    <div className="min-h-screen bg-cyber-dark text-white">
      {/* Header */}
      <header className="cyber-border border-x-0 border-t-0 p-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Shield className="h-8 w-8 text-cyber-blue animate-glow" />
            <h1 className="text-2xl font-bold text-cyber-blue">
              CYBER DEFENSE ASSISTANT
            </h1>
            <span className="text-sm text-gray-400">MILITARY SOC v1.0</span>
          </div>

          <div className="flex items-center space-x-4">
            <button
              onClick={() => setAutoRefresh(!autoRefresh)}
              className={`flex items-center space-x-2 px-4 py-2 rounded ${
                autoRefresh ? 'cyber-button' : 'cyber-button opacity-50'
              }`}
            >
              <RefreshCw className={`h-4 w-4 ${autoRefresh ? 'animate-spin' : ''}`} />
              <span>Auto Refresh</span>
            </button>

            <div className="flex items-center space-x-2 text-sm">
              <Activity className="h-4 w-4 text-cyber-green" />
              <span className="text-cyber-green">OPERATIONAL</span>
            </div>
          </div>
        </div>
      </header>

      <div className="flex h-[calc(100vh-80px)]">
        {/* Left Panel - Live Log Stream */}
        <div className="w-1/3 p-4 border-r border-cyber-blue/30">
          <div className="h-full flex flex-col">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-lg font-semibold text-cyber-blue flex items-center">
                <Eye className="h-5 w-5 mr-2" />
                LIVE LOG STREAM
              </h2>
              <span className="text-sm text-gray-400">{logs.length} entries</span>
            </div>

            <div className="flex-1 overflow-y-auto space-y-2 bg-cyber-gray/30 rounded-lg p-3">
              {logs.map((log, index) => (
                <div key={log.id || index} className="cyber-card text-xs">
                  <div className="flex items-center justify-between mb-1">
                    <span className={`px-2 py-1 rounded text-xs font-semibold ${getSeverityColor(log.severity)}`}>
                      {log.severity?.toUpperCase()}
                    </span>
                    <span className="text-gray-400">
                      {formatTimestamp(log.timestamp)}
                    </span>
                  </div>
                  <div className="text-cyber-blue font-medium">{log.event_type}</div>
                  <div className="text-gray-300 mt-1">
                    {log.source_ip} → {log.dest_ip}
                  </div>
                  <div className="text-gray-400 mt-1 text-xs truncate">
                    {log.message}
                  </div>
                </div>
              ))}

              {logs.length === 0 && (
                <div className="text-center text-gray-500 mt-8">
                  No logs available
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Middle Panel - AI Analysis and Charts */}
        <div className="w-1/3 p-4 border-r border-cyber-blue/30">
          <div className="h-full flex flex-col space-y-4">
            {/* Stats Overview */}
            <div className="cyber-card">
              <h3 className="text-md font-semibold text-cyber-green mb-2">THREAT OVERVIEW</h3>
              {stats && (
                <div className="grid grid-cols-2 gap-2 text-xs">
                  <div className="text-center">
                    <div className="text-cyber-red text-lg font-bold">{stats.last_24h_severity.critical}</div>
                    <div>Critical</div>
                  </div>
                  <div className="text-center">
                    <div className="text-red-400 text-lg font-bold">{stats.last_24h_severity.high}</div>
                    <div>High</div>
                  </div>
                  <div className="text-center">
                    <div className="text-cyber-yellow text-lg font-bold">{stats.last_24h_severity.medium}</div>
                    <div>Medium</div>
                  </div>
                  <div className="text-center">
                    <div className="text-cyber-green text-lg font-bold">{stats.last_24h_severity.low}</div>
                    <div>Low</div>
                  </div>
                </div>
              )}
            </div>

            {/* AI Analysis */}
            <div className="cyber-card flex-1">
              <h3 className="text-md font-semibold text-cyber-purple mb-2 flex items-center">
                <Zap className="h-4 w-4 mr-2" />
                AI THREAT ANALYSIS
              </h3>
              {analysis ? (
                <div className="space-y-3 text-sm">
                  <div>
                    <div className="text-cyber-blue font-medium">Summary:</div>
                    <div className="text-gray-300">{analysis.summary}</div>
                  </div>

                  <div>
                    <div className="text-cyber-blue font-medium">Threats Identified:</div>
                    <ul className="text-gray-300 text-xs space-y-1">
                      {analysis.threats_identified.map((threat, i) => (
                        <li key={i} className="flex items-start">
                          <span className="text-cyber-red mr-2">•</span>
                          {threat}
                        </li>
                      ))}
                    </ul>
                  </div>

                  <div className="flex items-center space-x-2">
                    <span className="text-cyber-blue font-medium">Overall Severity:</span>
                    <span className={`px-2 py-1 rounded text-xs font-semibold ${getSeverityColor(analysis.severity_classification)}`}>
                      {analysis.severity_classification?.toUpperCase()}
                    </span>
                  </div>
                </div>
              ) : (
                <div className="text-gray-500 text-center">Loading analysis...</div>
              )}
            </div>

            {/* Timeline Chart */}
            <div className="cyber-card h-48">
              <h3 className="text-md font-semibold text-cyber-yellow mb-2">ACTIVITY TIMELINE</h3>
              <ResponsiveContainer width="100%" height="100%">
                <AreaChart data={generateChartData()}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#2a3441" />
                  <XAxis 
                    dataKey="time" 
                    tick={{fontSize: 10}} 
                    stroke="#6b7280"
                    tickFormatter={(value) => new Date(value).toLocaleTimeString()}
                  />
                  <YAxis tick={{fontSize: 10}} stroke="#6b7280" />
                  <Tooltip 
                    contentStyle={{
                      backgroundColor: '#1a1f2e',
                      border: '1px solid #00d4ff',
                      borderRadius: '4px',
                      color: '#fff'
                    }}
                  />
                  <Area 
                    type="monotone" 
                    dataKey="count" 
                    stroke="#00d4ff" 
                    fill="#00d4ff" 
                    fillOpacity={0.2}
                  />
                </AreaChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>

        {/* Right Panel - Recommendations and Chat */}
        <div className="w-1/3 p-4">
          <div className="h-full flex flex-col space-y-4">
            {/* Recommendations */}
            <div className="cyber-card">
              <h3 className="text-md font-semibold text-cyber-green mb-2">RECOMMENDED ACTIONS</h3>
              {analysis?.recommendations ? (
                <div className="space-y-2">
                  {analysis.recommendations.map((rec, i) => (
                    <div key={i} className="flex items-center justify-between text-sm">
                      <span className="flex-1 text-gray-300">{rec}</span>
                      <button className="cyber-button-success text-xs ml-2">
                        EXECUTE
                      </button>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-gray-500 text-center text-sm">No recommendations available</div>
              )}
            </div>

            {/* Quick Actions */}
            <div className="cyber-card">
              <h3 className="text-md font-semibold text-cyber-red mb-2">QUICK ACTIONS</h3>
              <div className="grid grid-cols-2 gap-2">
                <button className="cyber-button-danger text-xs flex items-center justify-center">
                  <Ban className="h-3 w-3 mr-1" />
                  Block IP
                </button>
                <button className="cyber-button text-xs flex items-center justify-center">
                  <Shield className="h-3 w-3 mr-1" />
                  Isolate Host
                </button>
                <button className="cyber-button-danger text-xs flex items-center justify-center">
                  <AlertTriangle className="h-3 w-3 mr-1" />
                  Escalate
                </button>
                <button className="cyber-button-success text-xs flex items-center justify-center">
                  <Activity className="h-3 w-3 mr-1" />
                  Monitor
                </button>
              </div>
            </div>

            {/* Chat Interface */}
            <div className="cyber-card flex-1 flex flex-col">
              <h3 className="text-md font-semibold text-cyber-purple mb-2 flex items-center">
                <MessageSquare className="h-4 w-4 mr-2" />
                SOC ASSISTANT
              </h3>

              <div 
                ref={chatContainerRef}
                className="flex-1 overflow-y-auto space-y-2 bg-cyber-gray/30 rounded p-2 mb-2"
              >
                {chatHistory.map((msg, i) => (
                  <div key={i} className={`text-sm p-2 rounded ${
                    msg.type === 'user' 
                      ? 'bg-cyber-blue/20 text-cyber-blue ml-4' 
                      : 'bg-cyber-purple/20 text-cyber-purple mr-4'
                  }`}>
                    {msg.content}
                  </div>
                ))}

                {loading && (
                  <div className="bg-cyber-purple/20 text-cyber-purple text-sm p-2 rounded mr-4">
                    Analyzing...
                  </div>
                )}

                {chatHistory.length === 0 && (
                  <div className="text-center text-gray-500 text-sm mt-4">
                    Ask me about security events, threat analysis, or recommendations
                  </div>
                )}
              </div>

              <div className="flex space-x-2">
                <input
                  type="text"
                  value={currentMessage}
                  onChange={(e) => setCurrentMessage(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                  placeholder="Ask about threats, IPs, or recommendations..."
                  className="flex-1 bg-cyber-gray/50 border border-cyber-blue/30 rounded px-3 py-2 text-sm text-white placeholder-gray-400 focus:outline-none focus:border-cyber-blue"
                  disabled={loading}
                />
                <button
                  onClick={handleSendMessage}
                  disabled={loading || !currentMessage.trim()}
                  className="cyber-button flex items-center"
                >
                  <Send className="h-4 w-4" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
