import React, { useState, useEffect, useRef } from 'react';
import { 
  Shield, 
  AlertTriangle, 
  Activity, 
  MessageSquare, 
  Send,
  RefreshCw,
  Eye,
  Ban,
  Zap,
  TrendingUp,
  Server,
  Lock,
  Monitor,
  ChevronRight
} from 'lucide-react';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, BarChart, Bar } from 'recharts';
import api from './api';

function App() {
  const [logs, setLogs] = useState([]);
  const [analysis, setAnalysis] = useState(null);
  const [chatHistory, setChatHistory] = useState([]);
  const [currentMessage, setCurrentMessage] = useState('');
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(false);
  const [autoRefresh, setAutoRefresh] = useState(true);
  const [activeTab, setActiveTab] = useState('overview');
  const chatContainerRef = useRef(null);

  // Fetch data functions
  const fetchLogs = async () => {
    try {
      const data = await api.getLogs({ limit: 50 });
      setLogs(data);
    } catch (error) {
      console.error('Failed to fetch logs:', error);
    }
  };

  const fetchAnalysis = async () => {
    try {
      const data = await api.getAnalysis();
      setAnalysis(data);
    } catch (error) {
      console.error('Failed to fetch analysis:', error);
    }
  };

  const fetchStats = async () => {
    try {
      const data = await api.getStats();
      setStats(data);
    } catch (error) {
      console.error('Failed to fetch stats:', error);
    }
  };

  const handleSendMessage = async () => {
    if (!currentMessage.trim()) return;

    const userMessage = { type: 'user', content: currentMessage };
    setChatHistory(prev => [...prev, userMessage]);
    setCurrentMessage('');
    setLoading(true);

    try {
      const response = await api.chat(currentMessage);
      const aiMessage = { type: 'ai', content: response.answer };
      setChatHistory(prev => [...prev, aiMessage]);
    } catch (error) {
      const errorMessage = { type: 'ai', content: 'Sorry, I encountered an error. Please try again.' };
      setChatHistory(prev => [...prev, errorMessage]);
    }
    setLoading(false);
  };

  // Auto-refresh effect
  useEffect(() => {
    fetchLogs();
    fetchAnalysis();
    fetchStats();

    if (autoRefresh) {
      const interval = setInterval(() => {
        fetchLogs();
        fetchAnalysis();
        fetchStats();
      }, 10000); // Refresh every 10 seconds
      return () => clearInterval(interval);
    }
  }, [autoRefresh]);

  // Auto-scroll chat
  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [chatHistory]);

  const getSeverityClass = (severity) => {
    switch (severity?.toLowerCase()) {
      case 'critical': return 'status-critical';
      case 'high': return 'status-high';
      case 'medium': return 'status-medium';
      case 'low': return 'status-low';
      default: return 'status-low';
    }
  };

  const getLogEntryClass = (severity) => {
    const baseClass = 'log-entry';
    switch (severity?.toLowerCase()) {
      case 'critical': return `${baseClass} log-entry-critical`;
      case 'high': return `${baseClass} log-entry-high`;
      default: return baseClass;
    }
  };

  const formatTimestamp = (timestamp) => {
    return new Date(timestamp).toLocaleString();
  };

  // Generate chart data from logs
  const generateChartData = () => {
    if (!logs.length) return [];

    const hourlyData = {};
    logs.forEach(log => {
      const hour = new Date(log.timestamp).toISOString().slice(0, 13) + ':00:00Z';
      if (!hourlyData[hour]) {
        hourlyData[hour] = { time: hour, count: 0, critical: 0, high: 0 };
      }
      hourlyData[hour].count++;
      if (log.severity === 'critical') hourlyData[hour].critical++;
      if (log.severity === 'high') hourlyData[hour].high++;
    });

    return Object.values(hourlyData).sort((a, b) => new Date(a.time) - new Date(b.time));
  };

  // Generate severity distribution for bar chart
  const generateSeverityData = () => {
    if (!stats) return [];

    return [
      { name: 'Critical', value: stats.last_24h_severity.critical, color: '#ef4444' },
      { name: 'High', value: stats.last_24h_severity.high, color: '#f97316' },
      { name: 'Medium', value: stats.last_24h_severity.medium, color: '#f59e0b' },
      { name: 'Low', value: stats.last_24h_severity.low, color: '#10b981' },
    ];
  };

  return (
    <div className="min-h-screen bg-dark-950">
      {/* Professional Header */}
      <header className="app-header">
        <div className="px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-3">
                <div className="p-2 bg-gradient-to-br from-primary-500 to-primary-600 rounded-lg shadow-cyber">
                  <Shield className="h-6 w-6 text-white" />
                </div>
                <div>
                  <h1 className="text-xl font-semibold text-white">
                    Cyber Defense Operations Center
                  </h1>
                  <p className="text-sm text-slate-400">Advanced Threat Intelligence Platform</p>
                </div>
              </div>
            </div>

            <div className="flex items-center space-x-4">
              <div className="status-bar">
                <button
                  onClick={() => setAutoRefresh(!autoRefresh)}
                  className={`btn-secondary text-sm ${autoRefresh ? 'bg-primary-500/20 text-primary-400' : ''}`}
                >
                  <RefreshCw className={`h-4 w-4 mr-2 ${autoRefresh ? 'animate-spin' : ''}`} />
                  Auto Refresh
                </button>
              </div>

              <div className="status-bar">
                <Activity className="h-4 w-4 text-emerald-400" />
                <span className="text-emerald-400 font-medium text-sm status-operational">
                  OPERATIONAL
                </span>
              </div>
            </div>
          </div>
        </div>
      </header>

      <div className="flex h-[calc(100vh-88px)]">
        {/* Left Panel - Security Events */}
        <div className="w-1/3 p-6 border-r border-dark-700/50 overflow-hidden">
          <div className="h-full flex flex-col">
            <div className="pro-card-header">
              <div className="flex items-center space-x-2">
                <Eye className="h-5 w-5 text-primary-400" />
                <h2 className="text-lg font-semibold text-white">Live Security Events</h2>
              </div>
              <span className="px-3 py-1 bg-primary-500/10 text-primary-400 rounded-lg text-sm font-medium border border-primary-500/20">
                {logs.length} events
              </span>
            </div>

            <div className="flex-1 overflow-hidden">
              <div className="h-full overflow-y-auto space-y-3 p-6">
                {logs.map((log, index) => (
                  <div key={log.id || index} className={`${getLogEntryClass(log.severity)} animate-fade-in`}>
                    <div className="flex items-center justify-between mb-2">
                      <span className={getSeverityClass(log.severity)}>
                        {log.severity?.toUpperCase()}
                      </span>
                      <span className="text-xs text-slate-400 font-mono">
                        {formatTimestamp(log.timestamp)}
                      </span>
                    </div>

                    <div className="space-y-2">
                      <div className="flex items-center space-x-2">
                        <AlertTriangle className="h-4 w-4 text-primary-400 flex-shrink-0" />
                        <span className="text-white font-medium text-sm">{log.event_type}</span>
                      </div>

                      <div className="flex items-center space-x-2 text-xs">
                        <span className="text-slate-300 font-mono">{log.source_ip}</span>
                        <ChevronRight className="h-3 w-3 text-slate-500" />
                        <span className="text-slate-300 font-mono">{log.dest_ip}</span>
                      </div>

                      <p className="text-sm text-slate-400 line-clamp-2">{log.message}</p>
                    </div>
                  </div>
                ))}

                {logs.length === 0 && (
                  <div className="flex items-center justify-center h-full">
                    <div className="text-center">
                      <Monitor className="h-12 w-12 text-slate-600 mx-auto mb-3" />
                      <p className="text-slate-500">No security events found</p>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>

        {/* Center Panel - Analytics & Intelligence */}
        <div className="w-1/3 p-6 border-r border-dark-700/50 overflow-hidden">
          <div className="h-full flex flex-col space-y-6">
            {/* Threat Overview */}
            <div className="pro-card">
              <div className="pro-card-header">
                <h3 className="text-lg font-semibold text-white flex items-center">
                  <TrendingUp className="h-5 w-5 mr-2 text-emerald-400" />
                  Threat Overview
                </h3>
              </div>
              <div className="pro-card-content">
                {stats && (
                  <div className="grid grid-cols-2 gap-4">
                    <div className="metric-card">
                      <div className="metric-value text-red-400">
                        {stats.last_24h_severity.critical}
                      </div>
                      <div className="metric-label">Critical</div>
                    </div>
                    <div className="metric-card">
                      <div className="metric-value text-orange-400">
                        {stats.last_24h_severity.high}
                      </div>
                      <div className="metric-label">High</div>
                    </div>
                    <div className="metric-card">
                      <div className="metric-value text-amber-400">
                        {stats.last_24h_severity.medium}
                      </div>
                      <div className="metric-label">Medium</div>
                    </div>
                    <div className="metric-card">
                      <div className="metric-value text-emerald-400">
                        {stats.last_24h_severity.low}
                      </div>
                      <div className="metric-label">Low</div>
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* AI Threat Analysis */}
            <div className="pro-card flex-1">
              <div className="pro-card-header">
                <h3 className="text-lg font-semibold text-white flex items-center">
                  <Zap className="h-5 w-5 mr-2 text-purple-400" />
                  AI Threat Intelligence
                </h3>
              </div>
              <div className="pro-card-content">
                {analysis ? (
                  <div className="space-y-4 text-sm">
                    <div>
                      <h4 className="text-primary-400 font-medium mb-2">Executive Summary</h4>
                      <p className="text-slate-300 leading-relaxed">{analysis.summary}</p>
                    </div>

                    {analysis.threats_identified && analysis.threats_identified.length > 0 && (
                      <div>
                        <h4 className="text-primary-400 font-medium mb-2">Identified Threats</h4>
                        <ul className="space-y-2">
                          {analysis.threats_identified.map((threat, i) => (
                            <li key={i} className="flex items-start space-x-2">
                              <AlertTriangle className="h-4 w-4 text-red-400 flex-shrink-0 mt-0.5" />
                              <span className="text-slate-300">{threat}</span>
                            </li>
                          ))}
                        </ul>
                      </div>
                    )}

                    <div className="pt-3 border-t border-dark-700/30">
                      <div className="flex items-center justify-between">
                        <span className="text-slate-400 font-medium">Risk Assessment</span>
                        <span className={`${getSeverityClass(analysis.severity_classification)} text-xs`}>
                          {analysis.severity_classification?.toUpperCase()}
                        </span>
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="flex items-center justify-center h-full">
                    <div className="text-center">
                      <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-500 mx-auto mb-3"></div>
                      <p className="text-slate-500">Analyzing threats...</p>
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* Activity Timeline Chart */}
            <div className="chart-container h-64">
              <h3 className="text-lg font-semibold text-white mb-4 flex items-center">
                <Activity className="h-5 w-5 mr-2 text-primary-400" />
                Activity Timeline
              </h3>
              <ResponsiveContainer width="100%" height="100%">
                <AreaChart data={generateChartData()}>
                  <defs>
                    <linearGradient id="activityGradient" x1="0" y1="0" x2="0" y2="1">
                      <stop offset="5%" stopColor="#0ea5e9" stopOpacity={0.3}/>
                      <stop offset="95%" stopColor="#0ea5e9" stopOpacity={0}/>
                    </linearGradient>
                  </defs>
                  <CartesianGrid strokeDasharray="3 3" stroke="#334155" strokeOpacity={0.3} />
                  <XAxis 
                    dataKey="time" 
                    tick={{ fontSize: 10, fill: '#64748b' }} 
                    stroke="#475569"
                    tickFormatter={(value) => new Date(value).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                  />
                  <YAxis tick={{ fontSize: 10, fill: '#64748b' }} stroke="#475569" />
                  <Tooltip 
                    contentStyle={{
                      backgroundColor: '#1e293b',
                      border: '1px solid #475569',
                      borderRadius: '8px',
                      color: '#f1f5f9',
                      fontSize: '12px'
                    }}
                    labelFormatter={(value) => new Date(value).toLocaleString()}
                  />
                  <Area 
                    type="monotone" 
                    dataKey="count" 
                    stroke="#0ea5e9" 
                    fillOpacity={1}
                    fill="url(#activityGradient)"
                    strokeWidth={2}
                  />
                </AreaChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>

        {/* Right Panel - Actions & Chat */}
        <div className="w-1/3 p-6 overflow-hidden">
          <div className="h-full flex flex-col space-y-6">
            {/* Recommended Actions */}
            <div className="pro-card">
              <div className="pro-card-header">
                <h3 className="text-lg font-semibold text-white flex items-center">
                  <Lock className="h-5 w-5 mr-2 text-emerald-400" />
                  Recommended Actions
                </h3>
              </div>
              <div className="pro-card-content">
                {analysis?.recommendations ? (
                  <div className="space-y-3">
                    {analysis.recommendations.slice(0, 3).map((rec, i) => (
                      <div key={i} className="flex items-start justify-between space-x-3">
                        <span className="text-sm text-slate-300 flex-1">{rec}</span>
                        <button className="btn-success text-xs px-3 py-1 whitespace-nowrap">
                          Execute
                        </button>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="text-slate-500 text-sm">No recommendations available</p>
                )}
              </div>
            </div>

            {/* Quick Actions */}
            <div className="pro-card">
              <div className="pro-card-header">
                <h3 className="text-lg font-semibold text-white flex items-center">
                  <Server className="h-5 w-5 mr-2 text-amber-400" />
                  Quick Actions
                </h3>
              </div>
              <div className="pro-card-content">
                <div className="grid grid-cols-2 gap-3">
                  <button className="btn-danger text-sm flex items-center justify-center space-x-2">
                    <Ban className="h-4 w-4" />
                    <span>Block IP</span>
                  </button>
                  <button className="btn-secondary text-sm flex items-center justify-center space-x-2">
                    <Shield className="h-4 w-4" />
                    <span>Isolate</span>
                  </button>
                  <button className="btn-danger text-sm flex items-center justify-center space-x-2">
                    <AlertTriangle className="h-4 w-4" />
                    <span>Escalate</span>
                  </button>
                  <button className="btn-success text-sm flex items-center justify-center space-x-2">
                    <Activity className="h-4 w-4" />
                    <span>Monitor</span>
                  </button>
                </div>
              </div>
            </div>

            {/* AI Assistant Chat */}
            <div className="pro-card flex-1 flex flex-col min-h-0">
              <div className="pro-card-header">
                <h3 className="text-lg font-semibold text-white flex items-center">
                  <MessageSquare className="h-5 w-5 mr-2 text-purple-400" />
                  AI Security Assistant
                </h3>
              </div>

              <div className="flex-1 flex flex-col min-h-0 p-6">
                <div 
                  ref={chatContainerRef}
                  className="flex-1 overflow-y-auto space-y-3 mb-4"
                >
                  {chatHistory.map((msg, i) => (
                    <div
                      key={i}
                      className={msg.type === 'user' ? 'chat-message-user' : 'chat-message-ai'}
                    >
                      <div className="text-sm leading-relaxed">{msg.content}</div>
                    </div>
                  ))}

                  {loading && (
                    <div className="chat-message-ai">
                      <div className="flex items-center space-x-2">
                        <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-purple-400"></div>
                        <span className="text-sm">Analyzing security data...</span>
                      </div>
                    </div>
                  )}

                  {chatHistory.length === 0 && (
                    <div className="flex items-center justify-center h-full">
                      <div className="text-center">
                        <MessageSquare className="h-8 w-8 text-slate-600 mx-auto mb-2" />
                        <p className="text-slate-500 text-sm">Ask about threats, IPs, or security events</p>
                      </div>
                    </div>
                  )}
                </div>

                <div className="flex space-x-2">
                  <input
                    type="text"
                    value={currentMessage}
                    onChange={(e) => setCurrentMessage(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                    placeholder="Ask about security events, threats, or recommendations..."
                    className="input-field flex-1 text-sm"
                    disabled={loading}
                  />
                  <button
                    onClick={handleSendMessage}
                    disabled={loading || !currentMessage.trim()}
                    className="btn-primary px-3 py-2"
                  >
                    <Send className="h-4 w-4" />
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
